import { TextStyle, Page, Layout, EmptyState, FooterHelp, Link, Card } from '@shopify/polaris';
import { TitleBar, ResourcePicker } from '@shopify/app-bridge-react';
import { useState } from 'react';

const img = 'https://cdn.shopify.com/s/files/1/0757/9955/files/empty-state.svg'; 

const Index = () => {

    const [open, setOpen] = useState(false);

    const handleSelection = resources => {
        setOpen(false);
        console.log(resources);
    }

    return(

        <Page>

            <TitleBar
                primaryAction={{
                    content: 'Hola soy un primary action'
                }}
            />
            <ResourcePicker
                resourceType="Product"
                showVariants={false}
                open={open}
                onSelection={handleSelection}
                onCancel={() => setOpen(false)}
            />

            <Layout>
            <Layout.Section secondary>
                <Card title="Tienda" sectioned>
                <EmptyState
                    heading="Selecciona los productos"
                    action={{
                        content: 'Registro',
                        onAction: () => setOpen(true)
                    }}
                    image={img}
                >
                </EmptyState>
                </Card>
                <Card title="Tags" sectioned>
                <EmptyState
                    heading="Tienda"
                    action={{
                        content: 'Selecciona los Productos',
                        onAction: () => setOpen(true)
                    }}
                    image={img}
                >
                </EmptyState>
                </Card>
            </Layout.Section>
            </Layout>

            <Layout>

            </Layout>

            <FooterHelp>
            Soy Ricardo{' '}
            <Link url="https://help.shopify.com/manual/orders/fulfill-orders">
                Rodr√≠guez
            </Link>
            </FooterHelp>
        </Page>
    );
};

export default Index;
