import { TextStyle, Page, Layout, EmptyState, FooterHelp, Link, Card, Stack, Heading, Subheading, FormLayout, TextField, Button, Form } from '@shopify/polaris';
import { TitleBar, ResourcePicker } from '@shopify/app-bridge-react';
import { useState, useCallback } from 'react';

const register = () => {

    const [data, setStoreName] = useState({ name: 'sda', email: '', legalRep: '', creditCard: 0, phone: 0});
    const handleChangeStoreName = useCallback((name) => setStoreName(name), []);

    const handleChangeForm = (e) => {
        console.log(e.target);

    }

    return(
            <Page fullWidth={true}>
                <Layout>
                    <Layout.Section>
                        <Card>
                                <Heading element="h1">Registro</Heading>
                        </Card>
                    </Layout.Section>
                </Layout>
                <br/>
                <br/>
                <Form >
                    <FormLayout>
                        <Stack>
                            <TextField type="text" value=" LOL" label="Nombre de la tienda" value={data.name} onChange={handleChangeForm} />
                            <TextField type="email" label="E-Mail" value={data.email} onChange={handleChangeForm} />
                        </Stack>
                        <Stack>
                            <TextField type="text" label="Nombre del Representante Legal" value={data.legalRep} onChange={handleChangeForm} />
                            <TextField type="number" max="9999999999999999" label="Opcional (Tarjeta De Crédito" value={data.creditCard} onChange={handleChangeForm} />
                        </Stack>
                        <Stack>
                            <TextField type="number" label="Teléfono" value={data.phone} onChange={handleChangeForm} /> 
                        </Stack>
                        <Button >Guardar</Button>
                    </FormLayout>

                </Form>


            </Page>
    );
};
export default register;
