import { Card, Layout, Page, Button, Form, FormLayout, Stack, TextField } from '@shopify/polaris';
import { useState } from 'react';

const OtroLayout = () => { 

    const [descuento, setDescuento ] = useState('10%');

    const handleSubmit = () => {
        console.log("Formulario enviado", descuento)
    }

    const handleChange = () => {
        console.log("Formulario enviado", descuento)
    }
    return (
        <Page>
            <Layout>
                <Layout.AnnotatedSection
                    title="Algún otro título"
                    description="Alguna otra descripción"
                >
                    <Card sectioned>
                        <Form onSubmit={handleSubmit }>
                            <FormLayout>
                                <TextField 
                                    value={descuento}
                                    onChange={handleChange}
                                    label="Descuento"
                                    type="discount"
                                />
                                <Stack distribution="trailing">
                                    <Button primary submit>
                                        Guardar
                                    </Button>
                                </Stack>
                            </FormLayout>
                        </Form>
                    </Card>

                </Layout.AnnotatedSection>
            </Layout>
        </Page>
    )
}

export default OtroLayout;
