import { TextStyle, Page, Layout, EmptyState, FooterHelp, Link, Card, Stack, Heading, Subheading, FormLayout, TextField, Button, Form } from '@shopify/polaris';
import { TitleBar, ResourcePicker } from '@shopify/app-bridge-react';
import { useState, useCallback } from 'react';

const register = () => {

    const [name, setStoreName] = useState('');
    const handleChangeStoreName = useCallback((name) => setStoreName(name), []);

    const handleChangeForm = () => {

    }

    const [data, setSaveData] = useState([{name: '', email: '', legalRep, creditCard, phone}]);
    const handleSaveForm = useCallback( (event) => {
        setSaveData({
        name: name,
        email,
        legalRep,
        creditCard,
        phone
    });
    console.log(data);
    }, []);

    return(
            <Page fullWidth={true}>
                <Layout>
                    <Layout.Section>
                        <Card>
                                <Heading element="h1">Registro</Heading>
                        </Card>
                    </Layout.Section>
                </Layout>
                <br/>
                <br/>
                <Form onSubmit={handleSaveForm}>
                    <FormLayout>
                        <Stack>
                            <TextField type="text" label="Nombre de la tienda" value={name} onChange={handleChangeForm} />
                            <TextField type="email" label="E-Mail" value={email} onChange={handleChangeForm} />
                        </Stack>
                        <Stack>
                            <TextField type="text" label="Nombre del Representante Legal" value={legalRep} onChange={handleChangeForm} />
                            <TextField type="number" max="9999999999999999" label="Opcional (Tarjeta De Crédito" value={creditCard} onChange={handleChangeForm} />
                        </Stack>
                        <Stack>
                            <TextField type="number" label="Teléfono" value={phone} onChange={handleChangeForm} /> 
                        </Stack>
                        <Button submit>Guardar</Button>
                    </FormLayout>

                </Form>


            </Page>
    );
};
export default register;
