import { TextStyle, Page, Layout, EmptyState, FooterHelp, Link, Card, Stack, Heading, Subheading, FormLayout, TextField, Button, Form } from '@shopify/polaris';
import { TitleBar, ResourcePicker } from '@shopify/app-bridge-react';
import { useState, useCallback } from 'react';
import { useForm } from 'react-hook-form';

const register = () => {

    const {formRegister, handleSubmit} = useForm();

    const printRegister = dataForm => {
        console.log(dataForm);
    }

    return(
            <Page fullWidth={true}>
                <Layout>
                    <Layout.Section>
                        <Card>
                                <Heading element="h1">Registro</Heading>
                        </Card>
                    </Layout.Section>
                </Layout>
                <br/>
                <br/>
                <Form onSubmit={handleSubmit(printRegister)}>
                    <FormLayout>
                        <Stack>
                            <TextField type="text" label="Nombre de la tienda" value={formRegister} onChange={handleChangeStoreName} />
                            <TextField type="email" label="E-Mail" value={formRegister} onChange={handleChangeEmail} />
                        </Stack>
                        <Stack>
                            <TextField type="text" label="Nombre del Representante Legal" value={formRegister} onChange={handleChangeLegalRep} />
                            <TextField type="number" max="9999999999999999" label="Opcional (Tarjeta De Crédito" value={formRegister} onChange={handleChangeCreditCard} />
                        </Stack>
                        <Stack>
                            <TextField type="number" label="Teléfono" value={formRegister} onChange={handleChangePhone} /> 
                        </Stack>
                        <Button submit>Guardar</Button>
                    </FormLayout>

                </Form>


            </Page>
    );
};
export default register;
