import { TextStyle, Page, Layout, EmptyState, FooterHelp, Link, Card, Stack, Heading, Subheading, FormLayout, TextField } from '@shopify/polaris';
import { TitleBar, ResourcePicker } from '@shopify/app-bridge-react';
import { useState } from 'react';

const register = () => {

    const [storeName, setStoreName] = useState('Nombre de la tienda');
    const handleChangeStoreName = () => setStoreName(storeName);

    const [email, setEmail] = useState('E-Mail');
    const handleChangeEmail = () => setStoreName(storeName);

    const [legalRep, setLegalRep] = useState('Nombre del Representante Legal');
    const handleChangeLegalRep = () => setStoreName(storeName);

    const [creditCard, setCreditCard] = useState('Opcional (Tarjeta De Crédito');
    const handleChangeCreditCard = () => setStoreName(storeName);

    const [phone, setPhone] = useState('Teléfono');
    const handleChangePhone = () => setStoreName(storeName);

    return(
            <Page fullWidth={true}>
                <Layout>
                    <Layout.Section>
                        <Card>
                                <Heading element="h1">Registro</Heading>
                        </Card>
                    </Layout.Section>
                </Layout>
                <FormLayout>
                    <FormLayout.Group>
                        <TextField type="text" label="Nombre de la tienda" value={storeName} onChange={handleChangeStoreName} />
                        <TextField type="text" label="E-Mail" value={email} onChange={handleChangeEmail} />
                    </FormLayout.Group>
                    <FormLayout.Group>
                        <TextField type="text" label="Nombre del Representante Legal" value={legalRep} onChange={handleChangeLegalRep} />
                        <TextField type="text" label="Opcional (Tarjeta De Crédito" value={creditCard} onChange={handleChangeCreditCard} />
                    </FormLayout.Group>
                    <FormLayout.Group>
                        <TextField type="text" label="Teléfono" value={phone} onChange={handleChangePhone} /> 
                    </FormLayout.Group>
                </FormLayout>



            </Page>
    );
};
export default register;
