import { TextStyle, Page, Layout, EmptyState, FooterHelp, Link, Card, Stack, Heading, Subheading, FormLayout, TextField, Button, Form } from '@shopify/polaris';
import { TitleBar, ResourcePicker } from '@shopify/app-bridge-react';
import { useState, useCallback } from 'react';

const register = () => {

    const [name, setStoreName] = useState('');
    const handleChangeStoreName = useCallback((name) => setStoreName(name), []);

    const [email, setEmail] = useState('');
    const handleChangeEmail = useCallback((email) => setEmail(email), []);

    const [legalRep, setLegalRep] = useState('');
    const handleChangeLegalRep = useCallback((legalRep) => setLegalRep(legalRep), []);

    const [creditCard, setCreditCard] = useState('');
    const handleChangeCreditCard = useCallback((creditCard) => setCreditCard(creditCard), []);

    const [phone, setPhone] = useState('');
    const handleChangePhone = useCallback((phone) => setPhone(phone), []);

    const [data, setSaveData] = useState([{name: '', email: '', legalRep, creditCard, phone}]);
    const handleSaveForm = useCallback((_event) => {setSaveData({
        name,
        email,
        legalRep,
        creditCard,
        phone
    });
    console.log(data);
    }, []);

    return(
            <Page fullWidth={true}>
                <Layout>
                    <Layout.Section>
                        <Card>
                                <Heading element="h1">Registro</Heading>
                        </Card>
                    </Layout.Section>
                </Layout>
                <br/>
                <br/>
                <Form onSubmit={handleSaveForm}>
                    <FormLayout>
                        <Stack>
                            <TextField type="text" label="Nombre de la tienda" value={name} onChange={handleChangeStoreName} />
                            <TextField type="email" label="E-Mail" value={email} onChange={handleChangeEmail} />
                        </Stack>
                        <Stack>
                            <TextField type="text" label="Nombre del Representante Legal" value={legalRep} onChange={handleChangeLegalRep} />
                            <TextField type="number" label="Opcional (Tarjeta De Crédito" value={creditCard} onChange={handleChangeCreditCard} />
                        </Stack>
                        <Stack>
                            <p>+</p><TextField type="number" maxLength="12" label="Teléfono" value={phone} onChange={handleChangePhone} /> 
                        </Stack>
                        <Button submit>Guardar</Button>
                    </FormLayout>

                </Form>


            </Page>
    );
};
export default register;
