import { TextStyle, Page, Layout, EmptyState, FooterHelp, Link, Card, Stack, Heading, Subheading, FormLayout, TextField, Button } from '@shopify/polaris';
import { TitleBar, ResourcePicker } from '@shopify/app-bridge-react';
import { useState, useCallback } from 'react';

const register = () => {

    const [storeName, setStoreName] = useState('');
    const handleChangeStoreName = useCallback((storeName) => setStoreName(storeName), []);

    const [email, setEmail] = useState('');
    const handleChangeEmail = useCallback((email) => setEmail(email), []);

    const [legalRep, setLegalRep] = useState('');
    const handleChangeLegalRep = useCallback((legalRep) => setLegalRep(legalRep), []);

    const [creditCard, setCreditCard] = useState('');
    const handleChangeCreditCard = useCallback((creditCard) => setCreditCard(creditCard), []);

    const [phone, setPhone] = useState('');
    const handleChangePhone = useCallback((phone) => setPhone(phone), []);

    return(
            <Page fullWidth={true}>
                <Layout>
                    <Layout.Section>
                        <Card>
                                <Heading element="h1">Registro</Heading>
                        </Card>
                    </Layout.Section>
                </Layout>
                <br/>
                <br/>

                <FormLayout>
                    <FormLayout.Group>
                        <TextField type="text" label="Nombre de la tienda" value={storeName} onChange={handleChangeStoreName} />
                        <TextField type="text" label="E-Mail" value={email} onChange={handleChangeEmail} />
                    </FormLayout.Group>
                    <FormLayout.Group>
                        <TextField type="text" label="Nombre del Representante Legal" value={legalRep} onChange={handleChangeLegalRep} />
                        <TextField type="number" label="Opcional (Tarjeta De Crédito" value={creditCard} onChange={handleChangeCreditCard} />
                    </FormLayout.Group>
                    <FormLayout.Group>
                        <TextField type="text" label="Teléfono" value={phone} onChange={handleChangePhone} /> 
                        <Button fullWidth>Guardar</Button>
                    </FormLayout.Group>
                </FormLayout>



            </Page>
    );
};
export default register;
